INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336340, '-SPROUT-', '-SPROUT-, a Celeste-like speedrunning platformer.<br><br>After a mysterious portal opens, the ancient gems holding life itself in balance vanish. <br>With your home in ruins and the portal quickly fading, your only choice is to dive into the unknown and get them back. <br><br>Run, jump and dash your way through 100 levels across five unique areas, and restore your realm to its former glory.<br><br><strong>Features:</strong><br><ul class="bb_ul"><li>Snappy, speedrun-friendly gameplay<br></li><li>5 Areas with unique mechanics<br></li><li>Online Leaderboards<br></li><li>Original Soundtrack<br></li><li>20 Achievements <br></li><li>40 Hidden Collectibles<br></li><li>PS4/XBOX/Steam Full Controller Support</li></ul><br>After placing high in the last GM48 with the theme &quot;Small World&quot;, and with some additional time on my hands, I was inspired to turn this little jam game into a full release.<br><br>Short development logs will be posted on this page occasionally, we hope you all enjoy the game :)<br><br>Join the official -SPROUT- Discord: <a href="https://steamcommunity.com/linkfilter/?url=https://discord.gg/BRaNU7b" target="_blank" rel="noopener"  >https://discord.gg/BRaNU7b</a>', 4.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336340/header.jpg?t=1596461143', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336400, 'Heavy is the Hand that Holds the Six-Shooter', 'Embark on an adventure in the Wild West after you wake up in a coffin with amnesia and set out to find out who you are and what happened to you.<br><br><ul class="bb_ul"><li> Mouse driven controls<br></li><li> Real-time combat with one handed, two handed and thrown weapons.<br></li><li> Isometric viewport<br></li><li> Deep and emotional story<br></li><li> Engage in conversations with the inhabitants of the world and complete tasks for them<br></li><li> Survive by keeping an eye on your food and water levels and camp out for the night when necessary<br></li><li> Dynamic weather and day/night cycle<br></li><li> Non-linear gameplay in an open world<br></li><li> Care for your horse mount and your dog companion<br></li><li> Level up your character to increase your stats and unlock new skills</li></ul>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336400/header.jpg?t=1639080154', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336430, 'Waves of Rotting Flesh', 'Waves of Rotting Flesh is a stylized single player wave defense FPS. Defend a barricaded house against the hordes of the unliving, improve your abilities and weapons during the downtime between the enemy hordes, and try to survive until you can make a getaway. <br><br>The game features a selection of different weapons and enemies, various perks you can pick to improve your abilities, a regular play-mode and an endless mode. If you just want to kill some zombies for a few minutes, then this is the game for you.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336430/extras/zombi.gif?t=1594248522" />', 1.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336430/header.jpg?t=1594248522', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336460, 'Moirs', '<h2 class="bb_tag">《About》</h2>《Moirs》is a 7 Keys rhythm game, step into the world of music and enjoy the journey!<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336460/extras/Moirs.gif?t=1595169565" /><h2 class="bb_tag">《Features》</h2><ul class="bb_ul"><li>  3+4 Keys, Sky notes and Ground notes test your hand-eye coordination.<br></li><li>  7 original songs with their respective, elaborate background visuals.<br></li><li>  4 kinds of difficulty, having a relaxing or challenging game play.<br></li><li>  As songs unlock, the story of a girl chasing her music dream unfolds, and your game play decides the ending.<br></li><li>  Development diary and mini-game with lots of memes (?)</li></ul>', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336460/header.jpg?t=1595169565', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335680, 'The Kind Camomille', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1335680/extras/Glider.gif?t=1626291185" /><br><br>The Kind Camomille <br><br>A wholesome 3D exploration game about random acts of kindness and the joy of discovery.  Players take control of Camomille, a kind mouse on her way to her dear grandmother 100th birthday. On a relaxing and beautiful journey (that might not be so straight forward) she encounters people in need of a hand.  Along the way, players can uncover small hidden sceneries, find goodies to put in her giftbasket and make that birthday even more special.<h2 class="bb_tag">Key Features</h2> <br><ul class="bb_ul"><li> A Colorful, Kind and Relaxing Adventure.<br></li><li> No penalties, no death, just take your time.<br></li><li> Collect hidden items for a (non-obligatory) 100% ending.<br></li><li> A short game (about 2 hours), focused on discovery and light puzzle solving.</li></ul><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335680/extras/Deers.gif?t=1626291185" />', 8.19, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335680/header.jpg?t=1626291185', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335700, 'Marbleous Blocks', 'Marbleous Blocks is a point &amp; click, physics based, 3D-puzzle game with a retro vibe. <br><br>Join a marble named Max through a block building adventure and challenge your spatial thinking in interesting new ways. Select, rotate, move, and combine blocks to create new paths for Max. Together you must retrieve the missing energy orbs and restore the colors of the rainbow. <br><br>Even though Marbleous Blocks was designed to be a single player game there is still plenty of room for collaboration. The core of the game is about trying out different strategies and while you try one strategy your coplayer may think of a different one or complement yours. <br><br>The game consists of 45 puzzles of varying difficulty. Will you be able to solve them all?<br><br><strong>Create paths</strong><br>Move and rotate blocks (tetrominoes + friends) to create paths for Max. Creating paths requires energy cubes so you need to plan the paths, so you do not run out of energy.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335700/extras/rot.gif?t=1630934736" /><br><br><strong>Switch armors</strong><br>Max may wear different armors, each with its own set of abilities. Switch between armors to gain access to different parts of the world. <br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335700/extras/suitup.gif?t=1630934736" /><br><br><strong>Explore</strong><br>Planning is key, but since you may instantly undo your actions, exploration is encouraged and is an important aspect of the game play.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335700/extras/inspect.gif?t=1630934736" />', 5.69, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335700/header.jpg?t=1630934736', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335790, 'Operation: Tango', '<h1>FRIEND PASS</h1><p><a href="https://store.steampowered.com/app/1377150" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/1377150</a></p><br><h1>About the Game</h1><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335790/extras/Steam_gif_1.gif?t=1645555604" /><br><br>Join our Discord Server<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335790/extras/Steam_Assets_DiscordBanner_600X80.png?t=1645555604" /><br><br>NOTES:<br>- This game is online co-op only and requires a microphone.<br>- Thanks to the Friend Pass, any of your friends can play with you for free.<br>- Cross-platform play enabled<br><br>Operation: Tango is an espionage-themed cooperative adventure challenging you and a friend to complete dangerous missions across the globe in a high-tech near-future world. As an exclusive team of two, you and your partner will become AGENT and HACKER, working in tandem to bring a high-tech global menace to its knees. With only your voice to link you, teamwork is crucial, and communication is key!<br><br>Now with a FREE CONTENT UPDATE - The Challenge mode introduces a new area to explore, new puzzles to solve, and new robots to hack.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335790/extras/Steam_gif_2.gif?t=1645555604" /><h2 class="bb_tag">Game features:</h2><br><strong>CO-OP PUZZLE SOLVING</strong> - Combine skill sets from different points of view to overcome challenges focused on communication, not difficulty. These missions cannot be completed alone.<br><br><strong>THRILLING ESPIONAGE</strong> - Investigate, hack and infiltrate while executing missions across the globe. Live out your ultimate “Hollywood” spy fantasy!<br><br><strong>HI-TECH TOOLS</strong> - Experience the visual splendor and technological advances of a hyper-connected world built around augmented reality.<br><br><strong>FRIENDLY BANTER</strong> - Constant communication makes for both heartfelt and hilarious moments. It’s all about sharing your voice.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335790/extras/Steam_gif_3.gif?t=1645555604" /><h2 class="bb_tag">Multiplayer options:</h2><br><strong>FRIEND PASS</strong> - If you own Operation: Tango, any of your friends can play with you for free. All they need to do is download the Operation: Tango Friend Pass on their platform of choice. It takes two to save the world, but it only takes one to pay for it.<br><br><strong>CROSS-PLATFORM PLAY</strong> - Operation: Tango can be played with a person who’s not playing on the same platform as you. It just works.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335790/extras/Steam_gif_4.gif?t=1645555604" />', 16.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335790/header.jpg?t=1645555604', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335810, 'Bunny''s Maze', '<h1>Also recommended for you</h1><p><u><a href="https://store.steampowered.com/app/1431190/Bunnys_Trail/" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/1431190/Bunnys_Trail/</a></u><br><u><a href="https://store.steampowered.com/app/1494720/Mini_Moves_Among_Stars/" target="_blank" rel="noreferrer"  id="dynamiclink_1" >https://store.steampowered.com/app/1494720/Mini_Moves_Among_Stars/</a></u><br><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335810/extras/Follow.gif?t=1608690791" /></p><br><h1>About the Game</h1><strong>Bunny''s Maze</strong> is a <strong>colorful 3D Puzzle</strong> game, navigate two little rabbits and move the walls to create a path. Go through the maze and collect all the hidden carrots. Enjoy this <strong>cute and relaxing</strong> game for <strong>all ages</strong>.<br><br><strong>Features</strong><br><br><ul class="bb_ul"><li> 63 Levels </li><li> Simple Controls </li><li> Original soundtracks </li><li>Partial Controller Support </li><li>Cute and colorful graphics</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335810/extras/gameplayGif.gif?t=1608690791" /><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335810/extras/gelo.gif?t=1608690791" /><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335810/extras/ezgif.com-video-to-gif.gif?t=1608690791" /><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335810/extras/Follow.gif?t=1608690791" />', 1.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335810/header.jpg?t=1608690791', CURRENT_DATE(), 'GAME', NULL);
