INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336020, 'Mayor of Defense', 'Defense simulation game<br>1 to 4 players can play.<br>----------------------------------------------------------<br>-Story-<br><br>You are the village chief.<br>Just when you thought the promised slow life was waiting for you...<br><br>A monster that attacks without end!<br><br>Almost a one-man operation!<br><br>Black Village!<br><br><br>Work to get materials, money and hire mercenaries to defend your village.<br><br>----------------------------------------------------------<br>You can make an axe, pickaxe, hoe, and fishing rod on your workbench.<br><br>You can collect materials from trees with an axe and stones with a pickaxe.<br><br>The material will revive after a certain amount of time.<br><br>You can bury the seeds in the field with a mulberry when you have the seeds.<br><br>Eating food will increase your maximum SP by 1.<br><br>If you act with [X button], your SP is reduced by 1.<br><br>Resting at home will restore your SP.<br><br>If you run out of SP, you will fall down and a doctor will come to your aid.<br><br>But they will take money.<br><br>Merchants will appear several times a day.<br><br>You can sell wood, stone, food, or buy seeds.<br><br>The monsters will progress to the village.<br><br>When the monster reaches the village, the game is over.<br><br>Hire and intercept mercenaries for food and money.<br><br>Wages = mercenary''s HP.<br><br>The red gauge time period consumes SP, so go home to sleep.<br><br>Saving is done automatically in the morning.<br>----------------------------------------------------------<br>This game is gamepad recommended.<br>See in-game for instructions on how to use the gamepad.<br><br><br>-keyboard operation-<br><br>Movement: cross keys<br>Work: z<br>Decision: x<br>Cancel: c<br>Equipment Toggle: A Or D<br>Entering a house, using a workbench or furnace: s<br>Reset: F5', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336020/header.jpg?t=1593501586', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336040, 'Easter Pairs', 'A game in the classic &quot;memory&quot; genre. The images of eggs are laid out on the playing field, your task is to find paired ones. For speed and the least number of misses you get bonus points.  You can choose between playing fields of varying difficulty. More images - more load on your memory. Good game.', 1.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336040/header.jpg?t=1631373323', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336070, 'Ynglet: Prologue', '<h1>Ynglet</h1><p><a href="https://store.steampowered.com/app/1015920/Ynglet/" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/1015920/Ynglet/</a></p><br><h1>About the Game</h1>Ynglet: Prologue is a short and sweet introduction - a prologue if you will - to the the upcoming game, Ynglet by Nifflas!<h2 class="bb_tag"><strong>Features</strong></h2><strong>A Floating non Platformer</strong><br>Absolutely no platforms (which is really unusual for a platformer)!<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336070/extras/Gif_A.gif?t=1613653292" /><br><br><strong>Fluid Difficulty</strong><br>Find the level of challenge that suits you with granular difficulty settings. The ability to set quick respawn points at any time means repeating frustrating sections is never required.<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336070/extras/Gif_G.gif?t=1613653292" /><br><br><strong>Feel Good</strong><br>Dash into walls that reflect you. It feels really good and looks awesome!<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336070/extras/Gif_D.gif?t=1613653292" /><br><br><strong>Did I mention there are no platforms?</strong><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336070/extras/Gif_C.gif?t=1613653292" /><br><br><strong>Reactive Soundscape</strong><br>A highly reactive and dynamic soundtrack thanks to Ynglet''s custom and needlessly complicated music software.<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336070/extras/Gif_E.gif?t=1613653292" /><br><br><strong>Doodles for days</strong><br>The hand-drawn art style takes a fresh ‘illustrated’ approach, reminiscent of expressive doodles come to life. There’s lots of unusual creatures doing their own thing, making the world feel alive.<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336070/extras/Gif_F.gif?t=1613653292" /><br><br>I have made a lot of other games before like Knytt, Uurnog, and Affordable Space Adventures but I really hope you will like this game too!&quot; / Nifflas', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336070/header.jpg?t=1613653292', CURRENT_DATE(), 'GAME', NULL);
