INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335070, 'Breath of Fire Sound Collection', 'The original melodies of Capcom''s 1994 16-bit RPG classic <i>Breath of Fire</i> heat up Steam! <i>Breath of Fire Sound Collection</i> features 49 sweeping, grandiose tunes from Yasuaki Fujita, Mari Yamaguchi, Yoko Shimomura, and Minae Fujii that are sure to conjure up lots of nostalgia.<br><br>The 78+ minute soundtrack has been reauthored from the original, high quality source material. Formats include MP3 (V0), FLAC (16-bit), and AAC (VBR).', 7.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335070/header.jpg?t=1593101420', CURRENT_DATE(), 'MUSIC', NULL);
