INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1334490, 'Poly Bridge 2 Soundtrack', 'Adrian Talens returns with a new full-length Soundtrack! Relax to the soothing and critically acclaimed music with 13 completely new tracks plus 18 of the original Poly Bridge songs remastered! Enjoy over an hour and a half of the familiar and gentle acoustic guitar rhythms.', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1334490/header.jpg?t=1590683268', CURRENT_DATE(), 'MUSIC', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1334510, 'Jane Skriff', 'Jane Skriff is a 2d beat-em up platformer. After being used as part of a secret experiment, Jane Skriff must fight her way out of the despotic nation of Landia. She will combat elite forces, deadly assassins, regular soldiers, several people on pogo-sticks, and a guy in a motorcycle helmet.<br><br>Will she be able to escape with her life?<h2 class="bb_tag">Features</h2><ul class="bb_ul"><li>Free flowing combo system<br></li><li>Wide variety of enemies<br></li><li>Large hand-drawn world<br></li><li>Unique bosses<br></li><li>A story which involves the previously mentioned bosses</li></ul>', 3.29, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1334510/header.jpg?t=1599069790', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1334580, 'October Night Games', '<h1>Freezing Moon - Content update!</h1><p>We are happy to present you a new game mode. Freezing Moon is faster, more intensive and challenging than the original game.<br><a href="https://store.steampowered.com/news/app/1334580/view/2906476183738841094" target="_blank" rel="noreferrer"  ><img src="https://cdn.akamai.steamstatic.com/steam/apps/1334580/extras/coldMoonGif.gif?t=1646213000" /></a></p><br><h1>About the Game</h1>October Night Games is a digital tabletop game for 1-6 players about a battle between two mysterious cults. Every time the world stands at the crossroads, it is up to them to decide its fate.<br>The most known of those choices are done in the night when Halloween coincides with the full moon, giving the Outer Gods a chance to return to our realm in the full glory, and it is up to the cultists, whether to open the Gate.  But there are many other fateful decisions all around the year. <br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1334580/extras/OctoberGif.gif?t=1646213000" /><br>Both sides draw strength from rituals that worship the Outer Gods, but their goals are entirely different: the Changers fight for a radical transformation of the world, while the Keepers try to stop them. <br>You don’t know who is on which team, so you alongside with magic and alchemy you will need your social deduction skills. The only one whom you can completely trust is your loyal familiar with unique skills. <br>You have to perform bloody rituals, mix alchemical essences, and survive both the town folk and unholy monsters. Each playthrough promises tension, intrigue, and plenty of hexes. Are you ready to play with forbidden magic?<h2 class="bb_tag">Features list</h2><ul class="bb_ul"><li><strong>Literary inspirations</strong>: Roger Zelazny, H.P. Lovecraft, Abraham Merrit, Bram Stoker, Robert Bloch, and many others influenced our game''s procedurally generated narrative.<br></li><li><strong>Dastardly characters</strong>: Pick your own character, ranging from a mad monk to vampires, witches and more. Each packs unique abilities such as astral projection and the ability to drain life.<br></li><li><strong>Eldritch pets</strong>: Select your familiar, such as a loyal dog, a gossiping bat or a sneaky cat.<br></li><li><strong>Social deduction</strong>: Work out who is friend or foe. Each side will have its one wand Holder, Book Master and Lamp Bearer.<br></li><li><strong>Multiple locations</strong>: Travel the town, graveyard and hollow, looking for cursed ingredients for your alchemy and rituals.<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1334580/extras/gods.png?t=1646213000" /><br></li><li><strong>Summon horrors</strong>: Call on Cthulhu and beings even more inscrutable with your rituals.<br></li><li><strong>Make magic</strong>: Experiment with transmutation and craft essences using a challenging but accessible alchemy mechanic.<br></li><li><strong>Cursed combat</strong>: Fight off ghouls and angry townsfolk using a unique dice combat system, and perhaps even capture a minion or potential sacrifice.<br></li><li><strong>Stay alive</strong>: Keep your health, sanity and reputation intact - or there will be terrible consequences.<br></li><li><strong>Solo and multiplayer</strong>: Mix of competitive and cooperative play, hex your enemies and aid those you believe to be on your side.<br></li><li><strong>Atmospheric</strong>: Enjoy vintage hand-tinted silent movie aesthetics and an old school horror style soundtrack. <br></li><li><strong>Multiple replays</strong>: the combination of characters, familiars and allegiances is randomly generated with each game, along with the procedurally generated story.</li></ul>', 13.49, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1334580/header.jpg?t=1646213000', CURRENT_DATE(), 'GAME', NULL);
