INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332280, 'COVID-19  Epidemic Prevention', '通过酷鱼防疫宣传系列教学，用户可以避免去实体展馆带来的感染风险，只需要戴上VR设备就能真切地体验到身边常见的潜在灾害，学习关于新冠病毒的来源、传播途径及防治手段。', 169.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332280/header.jpg?t=1592297641', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332290, 'Spish', 'Fight alongside monsters to defeat your opponent in the arena<br><br>Control a character with a throwing spear against other player in this online PvP multiplayer game<h2 class="bb_tag">features:</h2><ul class="bb_ul"><li>Throwing spear as main weapon</li><li>Struck down chickens and fish to obtain item</li><li>Craft better monsters/spells</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332290/extras/spish_logo_sand_resized.png?t=1627206981" />', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332290/header.jpg?t=1627206981', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332310, 'New Town Needs Tamer', 'Become a champion tamer for a newly formed town, full of people eager to meet you.<br><br>Settle down into the tamer life, going out on adventures to tame all kinds of creatures and becoming acquainted with the people of the town.<br><br><strong>Features:</strong><br><ul class="bb_ul"><li> A number of creatures to tame.<br></li><li> 3 Alternative looks for each creature, plus 2 color schemes for each.<br></li><li> 12 Dateable characters.<br></li><li> 6 Zones to explore.<br></li><li> A number of minigame moves for your creatures.<br></li><li> Over 300 customization items for your character.<br></li><li> 3 Different UI styles for you to choose.</li></ul><br><i>Expect to see more as development continues...</i>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332310/header.jpg?t=1614328513', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332350, 'Phantom Proxy', 'Earth is gone. The only remains of humanity are kept alive in cyberspace afterlife.<br><br>Only the wealthiest individuals uploaded their souls into the hedonistic binary abyss.<br><br>Their servers maintained by the working class software living in sub-level decaying pixels.<h2 class="bb_tag">ITS TIME TO END THE AFTERLIFE</h2><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332350/extras/phantom_proxy_steam_gameplay.gif?t=1620846298" /><h2 class="bb_tag">INTENSE LOCK-ON ROGUE-LIKE COMBAT</h2><ul class="bb_ul"><li> Experience <strong>fast-paced</strong> arena-shooter combat with <strong>unique lock-on aiming system</strong><br></li><li> Fight in <strong>hand-crafted arenas</strong> with <strong>procedurally generated permutations</strong><br></li><li> Engage in combat against the deadly <strong>Cyber Security Task Force</strong> bots</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332350/extras/phantom_proxy_store_loot.gif?t=1620846298" /><br><br><ul class="bb_ul"><li> <strong>Find weapons and modify them with random loot</strong> that emphasizes player skill!</li></ul><h2 class="bb_tag">BE A PART OF THE COMMUNITY</h2><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332350/extras/store_description_Discord.png?t=1620846298" /><br>Communicate directly with the developer and fans of the game on the <strong>offical discord<strong>!<h2 class="bb_tag">OPEN DEVELOPMENT</h2><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332350/extras/phantom_proxy_wishlist.gif?t=1620846298" /><br>See how the game is made by watching <strong>developer live-streams</strong> on Twitch that include level design, programming, art, and sound creation!</strong></strong>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332350/header.jpg?t=1620846298', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332360, 'First Parsec', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1332360/extras/clips-v2-head.gif?t=1645450629" /><br>Take the helm and become a spaceship pilot tasked with saving your home world. Travel the galaxy using interconnected space-jumping gates, outsmart your enemies and fulfil your mission.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332360/extras/separator2.png?t=1645450629" /><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332360/extras/clips-v2-space.gif?t=1645450629" /><h2 class="bb_tag">Space</h2>Enjoy the freedom of flight, breath-taking views of entire galaxies and planetary clusters and explore them all. <br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332360/extras/separator2.png?t=1645450629" /><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332360/extras/clips-v2-weapons.gif?t=1645450629" /><h2 class="bb_tag">Weapons</h2>Arm yourself and wreak havoc on your enemies with plasma cannons, guided missiles, or time bombs! With your onboard 3D printer, new weapon types or even ships are at your fingertips – just insert the blueprint!<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332360/extras/separator2.png?t=1645450629" /><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332360/extras/clips-v2-combat.gif?t=1645450629" /><h2 class="bb_tag">Combat</h2>Face small and nimble enemy ships in fast-paced, intense combat that will test your agility and aiming skills. Plug in a controller or play the old-fashioned way, using a keyboard and mouse.', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332360/header.jpg?t=1645450629', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331400, 'The Oort Cloud 2020', 'A classic side-scrolling shoot´em up returns in a new look. Powered by the Unreal Engine, this 2.5D action scroller puts you in control of the IAV, the first interstellar attack vessel built by humans. The human race is under attack. A huge alien vessel is approaching our solar system fast and you need to stand against them. Fight the aliens in ten unique levels, get deeper into the alien ship and find out what drives the alien forces from within. Active abilities, such as time dilation (bullet-mode), cloak and homing missiles help to defeat even the cruelest enemies. Research new alien technology and improve your ship to get ready for bigger and stronger enemies. Once you defeat the final enemy, play again on a harder difficulty... new technology and more enemies await on each replay.<br><br><strong>Active Abilities</strong><br><ul class="bb_ul"><li> Slow down time with the time dilation device. This will make it easier to avoid enemy shoots and allow you to hunt down even the fastest of enemies. Maybe you can even avoid enemy missiles with this?<br></li><li> Cloak your ship and make it impossible for enemies to fire at you. You can still get hit, so be careful. Very helpful when there is little room to dodge enemy fire or you want to defend against enemy missiles.<br></li><li> Fire back homing missiles at your foes. These little helpers will make you life easier and help to clean the battle field faster.</li></ul><br><strong>Gamepad Support</strong><br>The Oort Cloud 2020 can be played with keyboard controls only but playing it with your Gamepad is ten times more fun. Using your gamepad will give you finer control about the ship, making it more fun to play.<br><br><strong>Tech Tree</strong><br>54 Skill Points wait to be spent on alien technology to improve your ship. With every level completed you will get more skill points (depending on difficulty) which you can then spent to make your ship more powerful. You will need them to beat the game on  the hard or extraterrestrira setting, so spend them wisely. <br><br><strong>Featuring 14 epic tracks from Fesliyan Studios</strong><br>The game features 14 epic tracks from Fesliyan Studios, providing the right musical background to set the scene. Tune up your speakers and enjoy.', 5.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331400/header.jpg?t=1591939624', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331510, 'Battle Talent', '<h1>Ver.0.0.2 Updated</h1><p>It’s just a quick fixed. Happy holidays everyone<br><br>Changed<br>* Increase the drop rate of coin<br>* Set EasySwing as default movement setup<br>* Optimize Tutorial level<br>* Fixed bugs in the sandbox custom button<br>Added<br>* Show tips when player use Oculus device running with SteamSDK, they should open Oculus.exe first to get better experience (not tested yet)<br>* Add version code on the message board<br>Removed<br>* The Climb Room( it’s not polished enough when using axe to climb)<br>What Coming Next:<br>* Try to add some parkour elements<br>* More language support<br>* Follow-up development outline</p><br><h1>About the Game</h1><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331510/extras/SteamPage.gif?t=1650462714" /><h2 class="bb_tag">What is Battle Talent</h2>Battle Talent  is a VR action game with Advanced Physics that satisfies your Combat Fantasy. <br>Let’s train yourself like a Warrior !<h2 class="bb_tag">It is suitable for:</h2><ul class="bb_ul"><li> players who like Creative Combat<br></li><li> like to Workout in VR<br></li><li> Challenge and Roguelite lover<br></li><li> VR enthusiast who wants to try Advanced Physics</li></ul><h2 class="bb_tag">Features</h2><ul class="bb_ul"><li> <strong><i>Advanced Physics</i></strong>: brings you accurate collision, climb, physics feedbacks and sounds, badass weapons and enemies…<br></li><li> <strong><i>Rich Combat Selections</i></strong>: aerial combo, deflect, grab, throw, dismember, shoot, magic…<br></li><li> <strong><i>Unique Weapons</i></strong>: many weapons have special skills, such as summoning lighting and ice<br></li><li> <strong><i>Roguelite Levels</i></strong>: massive seamless dungeon and lots of enemies, room events, loots waiting for you<br></li><li> <strong><i>Progress</i></strong>: you can earn coins and unlock new abilities and weapons in store as you played<br></li><li> <strong><i>Multiple Languages</i></strong> and <strong><i>Brutal Settings</i></strong> Supported</li></ul><h2 class="bb_tag">Ps</h2><ul class="bb_ul"><li> It is not easy to make a personal investment for two years, there is no form of crowdfunding, please join our Discord community, share, add a wish list to support us<br></li><li> The demo version only contains 90% of Trailer content<br></li><li> The image quality of Quest version is not exactly the same as Trailer<br></li><li> Seeking translation volunteers, your name will be shown in credits<br></li><li> We will checkout reviews and Discord almost everyday, please let us know if you have any disappointment or suggestion.<br></li><li> Before you start to play, make sure there are no people or monitor around you, In case of tragedy.</li></ul>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331510/header.jpg?t=1650462714', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331550, 'Big Ambitions', '<h1>Also check out our first game: Startup Company</h1><p><a href="https://store.steampowered.com/app/606800/Startup_Company/" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/606800/Startup_Company/</a></p><br><h1>About the Game</h1><h2 class="bb_tag">Prologue</h2><i>“It’s been 3 months since grandma died. I know that I’m an adult now that I’m 18 years old, but still… It feels so scary that no one is there to take care of stuff.<br><br>There’s one good thing though. At the funeral, my uncle Fred asked me for my phone number. He said he wanted to help me get on my feet. I don’t really know him, but I guess he’s family after all.”</i><h2 class="bb_tag">Game Description</h2>Big Ambitions is a roleplaying business simulator situated in the open-world city of New York. You start the game as a young kid surviving in the big apple. You have big ambitions for money and success, but you''re starting with absolutely nothing.<h2 class="bb_tag">Gameplay</h2>In Big Ambitions, you control a single person in the middle of the bustling metropolis of New York City. Your goal is to achieve financial success in various ways. Starting at the bottom flipping burgers or collecting garbage to eventually owning and running big businesses and real estate throughout the city.<br><br>As you progress, you always need to make sure that you''re getting enough sleep, keeping up with your health, and making sure your happiness is sufficient. Increasing your capital can be trickier than you think. As you will learn, money can''t buy time.<h2 class="bb_tag">Money making opportunities</h2>We''re still in the process of adding content to the game, but to give you a better understanding of the basics, we put together a list of ways you can make money:<br><br><br><ul class="bb_ul"><li> Getting a job (cashier, warehouse worker, garbage collector, etc)<br></li><li> Running storefront businesses (clothing shops, restaurants, gift shops, etc)<br></li><li> Running office-based businesses<br></li><li> Buying buildings and renting them out<br></li><li> Real estate investments (buying and selling buildings strategically)<br></li><li> Investment Funds</li></ul><h2 class="bb_tag">Sandbox</h2>Even though Big Ambitions has an interesting story featuring Uncle Fred and other characters, it''s up to you to decide how much you want to listen to them. Everything is available right from the beginning.<h2 class="bb_tag">Day to day objectives and tasks</h2>In Big Ambitions, you go through days just like in real life. Every day is different, and you''ll have various tasks to handle such as:<br><br><br><ul class="bb_ul"><li> Sleeping, eating, and exercising<br></li><li> Educating yourself with courses and school<br></li><li> Recruiting and managing employees<br></li><li> Managing business warehouse status (buying and transporting goods)<br></li><li> Expanding into new business areas<br></li><li> Renovating buildings<br></li><li> Managing your vehicle(s). Filling up gas, maintaining the vehicle, etc.<br></li><li> Paying taxes</li></ul>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331550/header.jpg?t=1644354602', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331560, 'Pandorex', '<h1>Just Updated</h1><p>The demo is available now!</p><br><h1>Follow us</h1><p>Have a chat with us and see what''s new. Join us here：<br><br><u><a href="https://twitter.com/lostdoggames" target="_blank" rel="noreferrer"  >Twitter</a><br><a href="https://steamcommunity.com/linkfilter/?url=https://discord.gg/xr7ns9G" target="_blank" rel="noopener"  >Discord</a><br><a href="https://steamcommunity.com/linkfilter/?url=https://www.instagram.com/lostdoggames/" target="_blank" rel="noopener"  >Instagram</a></u></p><br><h1>About the Game</h1>PANDOREX... Nobody really knows what is up with it.<br><br>About the story:<br>And then there is Harry. Harry is a simple, not very social guy. His best friend is his dog. Unfortunately he lost his dog. And since his dog is the only thing that is important to Harry, he starts looking for it, willing to do everything as long as he gets his dog back. And that´s where the story begins. Harry is only a very small piece of the puzzle, but in the end he could be the most important one. The one piece that holds the universe together or is responsible for the demise of the universe and therefore the demise of all life not only on earth, but on all the planets. <br><br>About the game:<br>You are responsible to aid Harry in his adventures, help him to choose the right decision and solve puzzles and riddles that come across Harrys long journey of finding his dog and hopefully not destroying the universe. Pandorex is a humorous and funny narrative-driven point and click adventure with a small tick of social criticism and a lot of pop culture and subgenre references. This game does not take itself very seriously, so you should not do it either!<br><br>Features:<br> - Narrative-driven point and click adventure<br> - Riddles and puzzles need to be solved to make progress<br> - Decision making: you have to decide what the character will do or say. Based on your decisions the story path may change.', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331560/header.jpg?t=1640268230', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331590, '7 summer days: Youth sky', '7 summer days: Youth Sky — a cycle of visual short stories within the extended universe of “7 summer days”. It tells about the adventures of an Nonamed trainee in the worlds of the Old Road. Includes four games, each with two routes:<br><br><ul class="bb_ul"><li> Youth Sky: Loss Story (a story of Lana and a story of Namiki)<br></li><li> Youth Sky: Passion Story (Lera''s and Stasya''s stories)<br></li><li> Youth Sky: Anger Story (Sasha''s and Yana''s stories)<br></li><li> Youth Sky: Road Daemon (a girl from Ildy story and Nonamed Crusade story).</li></ul><br>Chronologically, each subsequent short story follows the previous one. Inside one story, the narrative style allows you to create some kind of looping, so it does not matter which route to read first.<h2 class="bb_tag">Plot</h2><br>The protagonist of “Youth sky” is a young man of twenty-seven years old, living far from the best conditions (as he himself says, below the poverty line). One December morning he receives a message with a job offer and a promise of golden mountains. Looks like regular spam, doesn''t it?<br><br>But life flips about when he nevertheless agrees to the offer and ... gets on the Old Road. There he becomes a trainee of Zulum and travels through the worlds with his light hand, mastering some skills that are very necessary for the employer. For what purpose? It seems that the reader himself has to find out.<br><br>The Old Road universe consists of an endless galaxy of worlds, which would be difficult to describe, so the trainee will visit only some of them.<br><br>A little more about the worlds below, but the reasons why the Trainee will be there, Zulum is firmly kept secret, offering the &quot;unreasonable monkey&quot; to figure it out on his own. And it depends on the right choice of the Trainee where the story will turn, how it will end, and whether we will finally find out why the odyssey of the nameless Trainee was started at all.<h2 class="bb_tag">Ys worlds</h2><h2 class="bb_tag">World of wanderers</h2>The place of the prologue is New Year''s Eve in 2019. This is one of the fantasies - the world of tramps, known in Seven Days as the Russian Federation. It is tracing paper from our world and appears as a place uncomfortable and unkind, full of people who have forgotten how to dream, buried in smartphones and tablets. The Trainee flees from there almost by accident, and this becomes the impetus for truly miraculous events.<h2 class="bb_tag">Zeroed</h2>Zero reality, familiar to the reader from the “Seven Days of Summer”, this time will show us the parties beyond the camp. Specifically, in the first story, the reader will meet some people, who were announced in passing in 7dl. Acquaintances, relatives, friends and just people who have nothing to do with the heroines already beloved by readers ... Zeroed is the whole world. It has its own rules, distinctive features and special services that put the fantastic properties of the universe at the service of themselves and their state.<h2 class="bb_tag">Tech singularity</h2>The world of technical singularity is not at all like paradise, despite what seems to be one. The intern learns what is wrong with this world, helps one girl get home, and at the same time shows her the illusory nature of some things. And bring back her dream.<h2 class="bb_tag">Soft apocalipse(socpunk)</h2><h2 class="bb_tag">In progress</h2><h2 class="bb_tag">Shulgin''s world(alternative science)</h2><h2 class="bb_tag">In progress</h2><h2 class="bb_tag">A world of fate-crosses</h2><h2 class="bb_tag">In progress</h2><h2 class="bb_tag">A world of second chances</h2><h2 class="bb_tag">In progress</h2><h2 class="bb_tag">Ildizage</h2>A mistery synthetic city-world. Details in a separate article.', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331590/header.jpg?t=1596444498', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331600, 'Holodeck Arena', 'Holodeck Arena (formerly Source Wars) is a fast-paced arcade RTS with base-building, resource management and territorial conquest, set in a tron-like environment. Battles are fought in virtual arenas that are divided into sectors. Each sector has it''s own mainframe temple, which can be hacked to take over the sector. The game is won, once all sectors are hacked and all enemy units destroyed.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331620, 'The Last Survey', 'The Last Survey is a narrative essay where you take on the role of a geologist contracted by a Brazilian mining company to survey rare earth metal reserves. Rare earth metals like nickel, silver, cobalt, and platinum are used in most major electronics including PC parts, green technology components, and almost all mobile devices. However, after a year of conducting your research, you''ve found that there is not enough of these metals on the planet to sustain industries reliant on current supply chains, and you must deliver these findings to the company''s CEO, Victor Ferreira.<br><br>Based on studies and projections from real-world geoscientists and mineral analysts, recent findings show there isn''t enough mineral material on the planet for the growing demands for electric cars, high efficiency fuel cells, solar paneling manufacturing, and even some of the components used to play &amp; produce this game.<br><br>Will your choices help steer the direction of your employer away from global mineral fatigue? Or will the urgency of your research fall on unsympathetic ears?<h2 class="bb_tag">This Game Features</h2><ul class="bb_ul"><li>Over 1500 of hand-drawn digital, graphite, and charcoal drawings<br></li><li>Choose-Your-Own Adventure monologue / dialog options<br></li><li>Multiple endings depending on how you conduct yourself<br></li><li>Dynamic soundtrack by Lewis Kopenhafer<br></li><li>Story-rich narrative that critiques globalism, extractionist capitalism, and executive greed</li></ul><h2 class="bb_tag">Praise for The Last Survey</h2>&quot;A gorgeously animated short game about confronting the limited resources this planet &amp; a chilling CEO... its haunting story has really stuck with me.&quot;<br>-Eli Cauley of <a href="https://steamcommunity.com/linkfilter/?url=http://&quot;https://crawlspacestudio.itch.io&quot;" target="_blank" rel="noopener"  >Crawlspace Studio</a><br><br>&quot;<i>The Last Survey </i>is certainly one of the most poignant games I have played in a long while, and it is exquisitely illustrated to boot.”<br>-Dhruv of <a href="https://steamcommunity.com/linkfilter/?url=http://&quot;https://store.steampowered.com/search/?developer=Studio%20Oleomingus&quot;" target="_blank" rel="noopener"  >Studio Oleomingus</a><br><br>&quot;I can''t think of many other games that absolutely nail the atmosphere of serious corporate power. Plus the art style and soundtrack are excellent!&quot;<br>-David Cribb, maker of <a href="https://steamcommunity.com/linkfilter/?url=http://&quot;https://store.steampowered.com/app/1229030/A_Hand_With_Many_Fingers/&quot;" target="_blank" rel="noopener"  >A Hand with Many Fingers</a><br><br>&quot;If there’s still an ongoing argument about whether visual novels are ‘games’, then The Last Survey is the conclusive answer.&quot;<br>-John Walker, co-founder of Rock Paper Shotgun', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331620/header.jpg?t=1603213557', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331660, 'RONY - Rise Of New York', '<strong>RONY - Rise Of New York</strong> is a vertical shoot''em up set in a dark New York.<br><br>*****<br><br>New York in a near future.<br>Weakened by a pandemic.<br>Taken over by an alien breed.<br><br>*****<br><br>In this retro action-packed vertical shooter, set in a weakened Big Apple taken over by an alien breed, you have to save the earth by savvy navigating your pimped spaceship through New York’s exciting skyline. <br><br>Flooded by magma, New York and the world need your help. You and your gang take on the challenge in New York.<br><br>The game will contain 20+ locations, 4 player characters, 60 missions, 50+ enemies, a level editor and hours of fun. <br><br>Save the city of New York from the invasion of energy-hungry aliens. Free the streets from the lava-draining factories. Restore the city to its former glory.<br><br><strong>Be part of the Rise Of New York!</strong>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331660/header.jpg?t=1606219587', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331670, 'International Space Banana', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1331670/extras/Copy_of_ISB_LOGO_STEAM3.png?t=1644419537" /><br><br>We make one promise, and one promise only. Whether you are a:<br>✅ Bananaphobe <br>✅ Bananaphile <br>✅ Simply a banana yourself<br><br>This game will make you hate <strong><i><u>EVERYTHING</u></i></strong>. <br><br>...but <i><u>especially</u></i> bananas. Especially them.<h2 class="bb_tag">SPIN...Desperately</h2><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331670/extras/BANANA_GIF_3.gif?t=1644419537" /><h2 class="bb_tag">BOUNCE...Uncontrollably</h2><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331670/extras/BANANA_GIF_2.gif?t=1644419537" /><h2 class="bb_tag">STRUGGLE...Always</h2><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331670/extras/BANANA_GIF_1.gif?t=1644419537" /><h2 class="bb_tag">Key Features</h2><ul class="bb_ul"><li>Experience the unique frustrations of controlling a banana in zero-gravity.<br></li><li>Navigate a Spaceship riddled with Space-junk.<br></li><li>Ponder troubling existential questions, as commentary drives you to the brink. <br></li><li>Think about how lucky you are to have ended up as a banana and not some other fruit.<br></li><li>Figure out what you were supposed to be doing before you became a banana in the first place.<h2 class="bb_tag"><strong>Captain''s Log - Code B-A-N-A-N-A-N-A</strong></h2><i>&quot;...I don''t know what happened, but man, I''m a banana...<br><br>And I can move like a banana. I can rotate through zero gravity like only a banana can. I can latch onto things with my banana body. Thank the lord I am not a kiwi. If I were a kiwi I wouldn''t be able to swing my weight around like this and move.<br><br>And I mean to be honest it feels great. This is nice. I''m a nice yellow banana. No bruises. I''m actually a pretty attractive banana. I feel like one of... I feel like the best banana in the bunch. <strong><u>I FEEL LIKE THE BEST BANANA IN THE BUNCH</u></strong>!<br><br>But I''m the only banana in the bunch... Where is everyone?&quot;</i></li></ul>', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331670/header.jpg?t=1644419537', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331710, 'Pandemic Violence', 'Pandemic Violence is an exaggerated take on the world dealing with a deadly pandemic virus. Play through 10 levels at critical points during the pandemic spanning hundreds of years. As the title says, violence is the central point during this pandemic, so expect to get your hands dirty. Beat down citizens taken their gold, kill your neighbors for a roll of toilet paper, and plenty more scenarios like that.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330960, 'The Commission 1920: Organized Crime Grand Strategy', '<strong><h2 class="bb_tag">ABOUT THE GAME</h2></strong><br>The Commission 1920 is a turn-based strategy game set in the roaring 1920s where you play as a Don of a mafia family. The goal is to manage your resources and exert your influence on the city to remove competing powers using violence, politics, and careful investment of illegal rackets. The Commission 1920 is a remaster or &quot;definitive&quot; edition of the original The Commission game.<h2 class="bb_tag"><strong>FEATURES</strong></h2><br><strong>Choose your path:</strong> <br><br>Choose to align with one of five families, each with unique backgrounds, strengths, weaknesses, and connections to the city of New Shore. <br><br>The <strong>Bogiana family </strong>is a wealthy family that runs like a big business. They’ve established deep ties with the politicians of New Shore, and specialize in high-end rackets. They often take their success for granted, leaving their guard down: a key weakness that is exploited by other families. <br><br>The <strong>Calessi family</strong> is a family that is known for its code of honor and respectable operation (for a crime family, at least). Their strategy can be described as “strength in numbers”, and they have a growing network of associates throughout the city. They traditionally have had the strongest reputation with the Commission out of the five families.<br><br>The <strong>Donano family</strong> is a mysterious family that prefers to stay out of sight and out of trouble. They prefer not to get involved in conflicts, but that shouldn’t be mistaken for pacifism. Their influence on city politics and the media should not be underestimated. If you wrong them, you’ll find yourself with the whole city turned against you.<br><br>The <strong>Junio family</strong> is a boisterous family that is largely comprised of immigrants from the old country who settled in 1890s Fordham. They specialize in low-end rackets that don’t take much time or investment. They seem to find profits in places other families would deem a waste of time. They are the ever-present underdogs of New Shore.<br><br>The <strong>Rocca family</strong> is a dangerous family that has no qualms about engaging in high-risk rackets and killing those who get in their way. The Rocca family faces perpetual inner turmoil, but no one can deny their influence over the city. Nearly everyone knows someone who fell victim to the senseless violence of the Rocca family’s machinations.<br><br><br><strong>Expand Your Control:</strong> <br><br>Through moving the members of your family to different neighborhoods and boroughs, you can expand your family''s influence into New Shore and invest in a variety of illegal rackets. Expansion into neighborhoods and rackets owned by other families will result in conflict, requiring you to delicately balance between aggressive and peaceful actions. Gain intel on other families, and target enemy units for assassination or recruitment. <br><br><strong> Backstabbing and Loyalty:</strong> <br><br>Units have shifting loyalty based on the actions of the family. Units with that are disloyal can be recruited by enemy families and are more likely to sing to the authorities when arrested. Loyalty is treasured among the family. You can assign titles to your units, granting benefits, but also fueling the jealousy of their disloyal peers.<br><br><strong>Manage Capo Agendas:</strong> <br><br>You can manage the unique agendas of your caporegimes. Based on the family''s success against other families and obstacles, the caporegime''s loyalty will rise or fall.<br><br><strong>Become the Top Boss:</strong> <br><br>The families of New Shore will be ranked among mafia families from other cities. Those who rank among the top will lead the Commission and reap the benefits. Strive to be the boss of all bosses in the Commission ranking.', 12.49, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330960/header.jpg?t=1643900884', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331030, 'VR King of Battle Cards', '<h2 class="bb_tag"> Legend of the world Phantor</h2><strong> VR King of Battle Cards </strong> is a fantasy-style card game that will submerge you in the history of the world of Phantor.. The evil and cruel King Revelant invaded this world. Take part in the battles for the liberation of that world as part of the Union of Kings. Gather your army, learn how to use combat cards, increase the level of warriors and defeat Revelant in an epic battle, bringing peace to the lands of Phantor. Do the experiments with cards and collect the battle deck that is suitable for you. Archmage Niriton conjured the spirit of a warrior or magic with its own characteristics in each card, by knowing which you can improve their strengths or weaknesses. Before you face with Revelant, you must defeat his powerful supporters, putting and conjuring their souls in cards. At the same time, Revelant’s supporters are can challenge you to battle at any time using the “Black Mark” in an attempt to ruin your castle ... <br><strong> <i> The full version of the Legends of the world Phantor you will find in the book &quot;History&quot; on the main table in your castle in the game. </i> </strong><h2 class="bb_tag"> The mechanics of the game </h2> You appear in the throne room of your castle in the game and, moving between tables, you can set up your battle deck, buy new cards and improve existing cards for coins received from victories in battles.<br>In battle, you appear on the tower of your castle. Take your cards and throw them on the battlefield. At the place where the card falls, a warrior appears who will attack the enemy’s tower or the nearest enemy himself. The range of the card’s flight depends on the power of your throw. The further the warrior appears from the card, the longer the reanimator will fly to revive him.<br>When enemy warriors are coming you can shoot at them from a bow, making damage. Besides you, your side towers of the Princess shoot at enemy warriors too.<br>You need to destroy the central tower of the enemy to win the battle. Your warriors will attack it only after the destruction of the side towers. If the battle time is up, and the score is equal, you are given an extra time with accelerated energy production from the card reanimator, at the end of which you need to destroy the enemy’s towers. If time ends, and the score is equal, the defeat is scored to one whose towers’ health is less.<h2 class="bb_tag"> The development of the game </h2><ul class="bb_ul"><li>adding new cards;<br></li><li>adding historical battles with the supporters of Revelant;<br></li><li>game optimization;<br></li><li>adding of multiplayer;<br></li><li>improving the level of the game;<br></li><li>adding customization of the castle and the character;<br></li><li>work on game reviews and bugs.</li></ul>', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331030/header.jpg?t=1611471427', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331040, 'Card Apocalypse', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1331040/extras/capsule_main_workshop.jpg?t=1597645278" /><br>A rogulike Card Battle Game<br><br><strong>Background</strong><br>It''s said that finding the World Tree can realize a wish,people who keep looking for the World Tree are called travelers,they use their cards to fight the enemy.Their stories are all around the world.<br><br><strong> Play method</strong><br>Each skill card has numbers and colors,use numbers or color combinations to activate cards,the more powerful the card, the more difficult it is to take effect,each profession has a large number of exclusive skill cards and treasures.<br><br><strong>Features</strong><br>Distinctive characteristics of art style,players upgrade to unlock more skill cards and treasures,super many combinations waiting for players to discover.', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331040/header.jpg?t=1597645278', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331060, 'My Home Poltergeist', '<strong> Curse humans with Poltergeist! [/]<br><br>This game is a two-player battle game in which humans and ghosts are divided into battles.<br>The ghost side fulfills the conditions presented at the beginning of the game, and aims to move things in the house and curse humans.<br>However, if you move only the desired things, the contents will be revealed to humans.<br>Let''s camouflage well so that you do not find out what is the purpose of the curse ritual.<br>On the human side, the goal is to carefully observe what the ghost is moving so that the curse ritual is not achieved.<br><br>【Note】<br>* This game is for two players only. <strong> Please note that there is no single-person mode [/].<br>* The operation is basically a mouse + keyboard operation, but only Windows supports operation with the XBOX 360 controller.</strong></strong>', 6.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331060/header.jpg?t=1592550650', CURRENT_DATE(), 'GAME', NULL);
