INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (417461, ''Tom Clancy''''s Rainbow Six® Siege - The Safari Bundle'', ''<img src="https://cdn.akamai.steamstatic.com/steam/apps/417461/extras/Custom_Image.jpg?t=1575566900" /><br><br>Purchase this bundle and unlock 5 premium weapon skins to customize your loadout in coveted gold and animal skins.<br>The Safari bundle includes:<br>- The Gold weapon skin<br>- The Leopard weapon skin<br>- The Zebra weapon skin<br>- The Snake weapon skin<br>- The Tiger weapon skin'', 4.99, ''EUR'', ''https://cdn.akamai.steamstatic.com/steam/apps/417461/header.jpg?t=1575566900'', CURRENT_DATE(), ''DLC'', 359550);
