INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336290, 'ARTHA', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/WC.png?t=1633817547" /><br><strong>Welcome to Artha</strong><br>Get into the battle of ARTHA. Use your cards wisely and select the space on board to use them against your enemy. Choose the decks or create your own. Updates with free card sets and decks will bring it to the next level of the CCG world.<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/D2.gif?t=1633817547" /><br><br><strong>FEATURES?</strong><br>-Multiple collectible cards with abilities.<br>-Custom deck creation.<br>-Free updates with unique features.<br>-free card sets and decks updates.<br><br><strong>CARDS</strong><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/WR.png?t=1633817547" /><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/CRD_1.gif?t=1633817547" /><br>Warriors are the main heroes with the ability to deal with the damage. They come up with strength and abilities to protect other cards too.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/CR.png?t=1633817547" /><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/CRD_2.gif?t=1633817547" /><br>Creatures are a combination of magic and hero abilities. they just don''t deal with the damage but can heal other cards or use their abilities to destroy the cards or heal the friendly cards.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/MC.png?t=1633817547" /><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336290/extras/CRD_3.gif?t=1633817547" /><br>Magical cards are the cards that come with spells. They can be used to cheat with cards, their abilities, or increase the main player''s mana and health besides dealing with the damage.', 8.19, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336290/header.jpg?t=1633817547', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336420, 'Breaking Lockdown', '<h1>Discover the first chapter of my next game, for free!</h1><p><a href="https://store.steampowered.com/app/1555790/Wild_Dose/" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/1555790/Wild_Dose/</a></p><br><h1>David Wehle plays Breaking Lockdown!</h1><p>The creator of <a href="https://store.steampowered.com/app/555150/The_First_Tree/" target="_blank" rel="noreferrer"  >The First Tree</a> and <a href="https://store.steampowered.com/app/355970/Home_is_Where_One_Starts/" target="_blank" rel="noreferrer"  >Home is Where One Starts</a> made me the pleasure of testing my game!<br><a href="https://youtu.be/rMeZFXmvMjM?t=719" target="_blank" rel="noreferrer"  ><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336420/extras/YouTubeDavid_copier-2.jpg?t=1647381606" /></a></p><br><h1>What curators say</h1><p><a href="https://store.steampowered.com/app/1336420/Breaking_Lockdown/?curator_clanid=36640531" target="_blank" rel="noreferrer"  ><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336420/extras/horrornetwork.JPG?t=1647381606" /></a><br><br><a href="https://store.steampowered.com/app/1336420/Breaking_Lockdown/?curator_clanid=32839633" target="_blank" rel="noreferrer"  ><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336420/extras/haveaword.JPG?t=1647381606" /></a><br><br><a href="https://store.steampowered.com/app/1336420/Breaking_Lockdown/?curator_clanid=2677356" target="_blank" rel="noreferrer"  ><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336420/extras/artofgaming.JPG?t=1647381606" /></a></p><br><h1>About the Game</h1><strong>Challenging, gory, intense!</strong><br>Find your way through a hostile urban environment, avoid getting killed by authorities and prepare for anything, even corpses coming back to life!<br>You probably should have stayed home, but it''s too late for that...<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336420/extras/ZombieStreet_Transparency.gif?t=1647381606" /><h2 class="bb_tag">Key features:</h2><ul class="bb_ul"><li>Die and retry mechanics<br></li><li>A 30-60 minute experience that can be completed in one sitting<br></li><li>A rich (but dangerous!), detailed environment for players who love exploration<br></li><li>Secret endings, including a specific one for speed-runners!</li></ul><h2 class="bb_tag">Interested? Don''t forget to add the game to your wishlist:</h2><a href="https://store.steampowered.com/app/1336420/" target="_blank" rel="noreferrer"  id="dynamiclink_1" >https://store.steampowered.com/app/1336420/</a>', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336420/header.jpg?t=1647381606', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336430, 'Waves of Rotting Flesh', 'Waves of Rotting Flesh is a stylized single player wave defense FPS. Defend a barricaded house against the hordes of the unliving, improve your abilities and weapons during the downtime between the enemy hordes, and try to survive until you can make a getaway. <br><br>The game features a selection of different weapons and enemies, various perks you can pick to improve your abilities, a regular play-mode and an endless mode. If you just want to kill some zombies for a few minutes, then this is the game for you.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336430/extras/zombi.gif?t=1594248522" />', 1.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336430/header.jpg?t=1594248522', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336440, 'Enchanted Kingdom: The Secret of the Golden Lamp Collector''s Edition', '<strong>Domini Games</strong> presents the latest riveting adventure in the <i>Enchanted Kingdom</i> series!<br><br>Training to be a Forest Keeper is hard work, but with your dad as a teacher, you couldn‚Äôt be happier. That is until a dangerous genie appears, kidnapping your dad into another realm! You quickly find it‚Äôs no mere coincidence as you uncover a mystical side to your dad‚Äôs past you never could have dreamed of, one that will change your life forever. Can you stop a vengeful genie from tearing your world apart and save your dad before it‚Äôs too late? Find out in this spectacular Hidden-Object Puzzle adventure!<br><br>This is a special Collector''s Edition release full of exclusive extras you won''t find in the standard version. The Collector''s Edition includes:<br><br>‚Ä¢	Battle the most powerful genie of all time in the bonus chapter!<br>‚Ä¢	Brew magic potions and concoctions!<br>‚Ä¢	Collect animal traces, magic lamps, and more to learn more about the world!<br>‚Ä¢	Enjoy exclusive wallpapers, music, concept art, and more!<br>‚Ä¢	Complete in-depth strategy guide included!<br><br><br>This is a Hidden Object Puzzle Adventure game, meaning a specific genre of story-driven, point &amp; click adventures. Throughout the game, players are tasked to find a list of objects hidden within a particular scene, and to occasionally complete various mini-games/puzzles.', 8.19, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336440/header.jpg?t=1590789544', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336460, 'Moirs', '<h2 class="bb_tag">„ÄäAbout„Äã</h2>„ÄäMoirs„Äãis a 7 Keys rhythm game, step into the world of music and enjoy the journey!<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336460/extras/Moirs.gif?t=1595169565" /><h2 class="bb_tag">„ÄäFeatures„Äã</h2><ul class="bb_ul"><li>  3+4 Keys, Sky notes and Ground notes test your hand-eye coordination.<br></li><li>  7 original songs with their respective, elaborate background visuals.<br></li><li>  4 kinds of difficulty, having a relaxing or challenging game play.<br></li><li>  As songs unlock, the story of a girl chasing her music dream unfolds, and your game play decides the ending.<br></li><li>  Development diary and mini-game with lots of memes (?)</li></ul>', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336460/header.jpg?t=1595169565', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336500, 'Lazy Sweet Tycoon', 'Welcome Tycoons!<br><br>To our Cross-Play Casual Clicker.<br>Free cloud saving cross-play available between Steam and iOS/Android, so you can play on the move!<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336500/extras/crossplay.gif?t=1629462114" /><br><br>Do you have what it takes to become a Candy CEO, Factory Builder and Menacing Micro-Manager? <br><br>In a sea of tactile taps, pop the profit out of the world as you manage your valuable sugar resource, energising the island as you tap. But will you be quick enough to successfully profiteer? <br><br>When you''re not furiously tapping: Develop your sweet empire in this voxel world. Manage your island and employees, hire and manage transport vehicles, complete jobs and fight against other players to top the online leaderboards and be the prized No.1 CEO.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336500/extras/upgrade.gif?t=1629462114" /><br><br><ul class="bb_ul"><li> Invest your money with Common, Rare, Epic and Legendary Upgrades!<br></li><li> Research powerful perks and abilities<br></li><li> Develop your recipe from Plain Cookies into Edible Nanobots or Sugar Rockets<br></li><li> Build up your island with advanced tech and industry<br></li><li> Improve by upgrading your buildings from wood, to steel, to diamond and more!</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336500/extras/manage.gif?t=1629462114" /><br><br><ul class="bb_ul"><li> Play your way with customisable passive and active strategies!<br></li><li> Hire or fire your staff. Boost salary or cut wages. <br></li><li> Spawn an empire of Golden Sheep or a Fleet of Planes.<br></li><li> Personalise your playstyle and optimise your factory with a vast Research Tree<br></li><li> Charter lorries, boats and planes to export your sweets across the globe!</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336500/extras/customise.gif?t=1629462114" /><br><br><ul class="bb_ul"><li> Customise your island with unique themes, staff, vehicles and more<br></li><li> Get custom profile banners and borders and stand out from the crowd!<br></li><li> Play during seasonal events to unlock rare skins for your world<br></li><li> Mix and match your island to stand out from the crowd!</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336500/extras/achieve.gif?t=1629462114" /><br><br><ul class="bb_ul"><li> Battle your way to the all-time top with Trophy leaderboards<br></li><li> Become the most efficient CEO of the week in our PVP Efficiency Contests<br></li><li> Earn your way up, and gather medals to show off on your player profile</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336500/extras/prestige.gif?t=1629462114" />', 6.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336500/header.jpg?t=1629462114', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335630, 'IMHA', '<strong>IMHA is an action horror FPS set on a mysterious island ravaged by a demon invasion.</strong> <br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335630/extras/gif3.gif?t=1644602452" /><br><br>You are washed up on the shores of a mysterious island, attacked by  deadly monsters, to survive you must battle your way through hordes of enemies. Your path will lead you through abandoned villages, dark forests, dangerous swamps and ancient ruins.<br>IMHA is a passion project developed from scratch by a one-man team. This horror FPS features dynamic pacing and spawn system providing unique challenges every time you play.<br><br><ul class="bb_ul"><li>Fight through endless hordes of deadly enemies.<br></li><li>Survive an apocalyptic invasion of demonic monsters.<br></li><li>Explore a ravaged mysterious island, find random supplies.<br></li><li>Overcome challenging unpredictable boss encounters.<br></li><li>Experience an ever-changing game world with dynamic pacing.</li></ul>', 5.69, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335630/header.jpg?t=1644602452', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335700, 'Marbleous Blocks', 'Marbleous Blocks is a point &amp; click, physics based, 3D-puzzle game with a retro vibe. <br><br>Join a marble named Max through a block building adventure and challenge your spatial thinking in interesting new ways. Select, rotate, move, and combine blocks to create new paths for Max. Together you must retrieve the missing energy orbs and restore the colors of the rainbow. <br><br>Even though Marbleous Blocks was designed to be a single player game there is still plenty of room for collaboration. The core of the game is about trying out different strategies and while you try one strategy your coplayer may think of a different one or complement yours. <br><br>The game consists of 45 puzzles of varying difficulty. Will you be able to solve them all?<br><br><strong>Create paths</strong><br>Move and rotate blocks (tetrominoes + friends) to create paths for Max. Creating paths requires energy cubes so you need to plan the paths, so you do not run out of energy.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335700/extras/rot.gif?t=1630934736" /><br><br><strong>Switch armors</strong><br>Max may wear different armors, each with its own set of abilities. Switch between armors to gain access to different parts of the world. <br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335700/extras/suitup.gif?t=1630934736" /><br><br><strong>Explore</strong><br>Planning is key, but since you may instantly undo your actions, exploration is encouraged and is an important aspect of the game play.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335700/extras/inspect.gif?t=1630934736" />', 5.69, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335700/header.jpg?t=1630934736', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335910, 'Droid Guier', 'Droid Guier is a battle of robots, where the player will be able to control an Android, use several weapons to destroy his enemies, but use with wisdom and strategy to not be unarmed. The player can win several weapons at each stage, use the defense and attack weapons, leave several traps to corner his enemies. The game also has a weapon shop that will be unlocked by the player and will be able to choose and buy with gold screws collected at each stage of the game.<br>The game has great gameplay, attack speed and addictive defense that gives a sense of pleasure to each level conquered. In different phases there are puzzles to do, many are optional, the player chooses if he wants to make the sequences and win the prizes. There are 47 levels with different enemies and different obstacles.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335910/extras/GIF02.gif?t=1640549778" /><br><br>The main Droid can use various weapons and resources<br><br>- Shoot cannonballs at enemies;<br>- Install explosive mines and corner opponents;<br>- Use energy hitters to destroy nearby robots;<br>- Push boxes and explosive mines;<br>- Use the turbo and have more speed in the game;<br>- Use shields to protect yourself from enemy attacks;<br><br>The main Droid can pick up multiple items<br><br>- Gold screws that you can collect and buy different weapons;<br>- destroy boxes found in the levels and take the items inside them;<br>- in a certain time, boxes will fall that can help in the battle.', 0.79, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335910/header.jpg?t=1640549778', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335970, 'Greedy Maze', 'A casual next level maze game with 150 levels, in 5 different maze categories, with 3 full customizable modes and 12 color themes for anyone who likes maze solving. The main idea was to add some new features such as  moving walls and a 3D weave type of maze.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335970/extras/gifMaze.jpg?t=1608658658" /><br><br>I visualized the classic dark mode and progress towards completion. Therefore I decided to show the whole maze for a few seconds so that the player could remember the path<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335970/extras/dark.jpg?t=1608658658" /><br><br>Can you solve all?<br><br><strong>Features:</strong><br><br><ul class="bb_ul"><li>12 themes</li></ul><ul class="bb_ul"><li>5 different types of mazes</li></ul><ul class="bb_ul"><li>30 levels for each type</li></ul><ul class="bb_ul"><li>3 different modes that can be used together</li></ul><br><strong>Controls:</strong><br><br><ul class="bb_ul"><li>W,A,S,D</li></ul><ul class="bb_ul"><li>Keyboard arrows</li></ul><ul class="bb_ul"><li>Game pad</li></ul>', 2.39, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335970/header.jpg?t=1608658658', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336040, 'Easter Pairs', 'A game in the classic &quot;memory&quot; genre. The images of eggs are laid out on the playing field, your task is to find paired ones. For speed and the least number of misses you get bonus points.  You can choose between playing fields of varying difficulty. More images - more load on your memory. Good game.', 1.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336040/header.jpg?t=1631373323', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336060, 'TAISHO x ALICE episode 2', 'DUALICE(TAISHO x ALICE episode II) is a fairytale visual novel in which YOU must save your Prince Charming!<br>The game is fully voiced in Japanese (excluding the main character).<br><br>As you progress through the story, you will be asked to make choices at key points that will lead you to different endings. You will take on the role of the fairytale heroine in order to rehabilitate your chosen love interest and guide him to his &quot;happily ever after&quot;!<br><br>At the end of the prologue, you will choose your prince and enter the Looking-Glass World. Each love interest has his own standalone story set in an alternate universe where the relationships between the characters will vary.<br><br>*Episode 2 features two love interests: Kaguya and Gretel.<br><br><br>[Content Warning]<br>Please be advised that this game deals with mature themes including mental illness, self-harm, suicidal ideation, and sexual assault.<br><br><br><br>[STORY]<br>Once upon a time, in a topsy-turvy world...<br><br>In the beginning, you find yourself lost within a world of utter darkness. Your memories are gone; even your name is a mystery.<br>But just when the bitter loneliness threatens to break your spirit, you meet Alice‚Äîa blond-haired, blue-eyed young man who promptly christens you &quot;Arisu.&quot;<br>Like you, Alice cannot remember anything beyond his own name. Ignoring his protests, you drag him along with you through the darkness... until you happen upon a massive mirror made of crystal.<br>You stumble through the glass and into the Looking-Glass World, an antiquated fairytale dreamscape that feels oddly familiar...<br>There, amid a colorful cast of gender-swapped fairytale characters, you take on the role of the heroine and join them in their twisted tales.<br><br>How will the story end? It''s up to you!<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336060/extras/chara03_eng.png?t=1647246275" /><br>ONCE UPON A TIME, a dramatic breakup unfolds at the cafe where you work.<br>Everyone is scandalized‚Äîbut then the guy turns to you and says, &quot;Fancy going out with me?&quot; So obviously, you say yes!<br>His name is Kaguya, and he''s your new boyfriend. He''s everything you could possibly want: sweet, caring, and... HOMELESS?! Day 1 of the relationship, and you''re already living together!<br>Where will it go from here?!<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336060/extras/chara04_eng.png?t=1647246275" /><br>ONCE UPON A TIME, you awaken to find yourself in a strange, unfamiliar log cabin.<br>But don''t worry! Here you have food, clothing, and everything else you need to survive. Only one small problem: you''re tied up on the bed...<br>&quot;We''re going to live here from now on, where it''s safe.&quot;<br>So begins a life of confinement with your younger brother Gretel.<br>What inspired him to take you captive?<br>What is his true objective? And can you make it out alive?!<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336060/extras/underline.png?t=1647246275" /><br>„ÄêVOICE„Äë<br>CINDERELLA -- Daisuke Hirakawa<br>RED RIDING HOOD -- Tomoaki Maeno<br>KAGUYA -- Toshiki Masuda<br>GRETEL -- Takuya Eguchi<br>SNOW WHITE -- Syouta Aoi<br>WIZARD -- Wataru Hatano<br>ALICE -- Yoshitugu Matsuoka<br>WOLF -- Natsuki Hanae<br>HUNTSMAN -- Tomohisa Hashizume<br><br><br><a href="https://store.steampowered.com/app/1056570/TAISHO_x_ALICE_episode_1/" target="_blank" rel="noreferrer"  ><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336060/extras/prev1_ench.png?t=1647246275" /></a><br><a href="https://store.steampowered.com/app/1515890/_episode_3/" target="_blank" rel="noreferrer"  ><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336060/extras/next3_ench.png?t=1647246275" /></a><br><a href="https://store.steampowered.com/app/1596540/_epilogue/" target="_blank" rel="noreferrer"  ><img src="https://cdn.akamai.steamstatic.com/steam/apps/1336060/extras/next4_ench.png?t=1647246275" /></a>', 16.79, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1336060/header.jpg?t=1647246275', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1336100, 'Arcadium', 'In Arcadium the main goal is to break all the bricks on each level using a paddle and a ball, if the ball passes the paddle and leaves the screen you lose a life. There are however some special levels where the goal is something else and also levels where you fight a boss.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1335230, 'Sumire', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1335230/extras/SteamHeader.png?t=1650869264" /><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335230/extras/gif_1.gif?t=1650869264" /><br><strong>Sumire</strong> is an indie narrative adventure, featuring a one-day journey through a picturesque Japanese village. In this enchanted place, a disenchanted girl named Sumire meets a mischievous spirit who makes her a deal to fulfill her most desired wish.<br>But in order to see this wish come true, she must complete a series of tasks in return, before night falls and this special day reaches its end.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335230/extras/storedivider4-en.png?t=1650869264" /><h2 class="bb_tag">A Narrative Quest-Driven Adventure</h2><ul class="bb_ul"><li>Follow Sumire on a series of quests both big and small, given by charming forest creatures, quirky townspeople, and Sumire''s own heartfelt needs. <br>Take or refuse them, but be wary: as the day progresses, your path will change and you may never get the same chance again.<br></li><li>Discover collectibles, bonus items, tests of skill, and other challenges -- some hidden, some not.<br></li><li>Explore a variety of lush, beautiful environments rendered in a painterly style.<br></li><li>A one day time limit. With the sky changing from the promise of morning to the soft twilight, all choices are finished when the sky turns to violet.</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335230/extras/storedivider2.png?t=1650869264" /><h2 class="bb_tag">Between the Magical and the Mundane</h2><ul class="bb_ul"><li>For a single day, the magical world crosses with ordinary life for Sumire.<br></li><li>Within this ambiguous time, be extra mindful of words, choices, and actions taken. <br></li><li>What awaits at the end of the day...?<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335230/extras/storedivider3.png?t=1650869264" /><h2 class="bb_tag">A Day to Remember, and Choices to be Made</h2></li><li>Guide Sumire through her reality, her dreams, and her memories.<br></li><li>As the sky changes throughout the day, Sumire too must come to terms with the changes that have affected her life, as well as those around her.<br></li><li>How Sumire ultimately addresses these matters is influenced by the player''s guidance, seeking to turn a life unlived into a final dream come true.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1335230/extras/gif_2_EN.gif?t=1650869264" /></li></ul>', 8.19, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1335230/header.jpg?t=1650869264', CURRENT_DATE(), 'GAME', NULL);
