INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331310, 'Card escape: Plane crash', 'There was a disaster, your plane crashed into the very center of the forest. And now we need to get out healthy. You will find a lot of cards with different effects, both negative and positive. Also, with the help of crafting, you can simplify your life, protect yourself from predators and get food for yourself. Survive...', 0.79, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331310/header.jpg?t=1643184439', CURRENT_DATE(), 'GAME', NULL);
