INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1333280, 'Klomanager - Regurgitated', 'If toilets are your passion, you have come to the right game. If not, you still might have a lot of fun with this economic simulation filled with crazy ideas and characters. Build up your toilet empire and research equipment to increase the attraction of your toilets. To deal with the competition you can send pukers and destroyers on sinister missions. Or ruin their reputation by using gossipers who spread malicious rumours about the state of your opponents'' toilets.<br><br>Features:<br><br><ul class="bb_ul"><li>Get dirty rich with other peoples needs<br></li><li>Research 24 cutting edge toilet technologies<br></li><li>Equip your toilets and expand your toiletitarian system<br></li><li>Sabotage against your opponents with pukers, destroyers and gossipers<br></li><li>Use the sanitary board to strike fear in the hearts of your opponents<br></li><li>Improve the reputation of your toilets with advertising campaigns<br></li><li>Explore seven different goals with multiple settings<br></li><li>Random events and tempests add variation to each match<br></li><li>Play on different maps <br></li><li>Up to three AI or human opponents (hot seat game or Steam Remote Play)<br></li><li>Get all game intern achievements</li></ul>', 4.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1333280/header.jpg?t=1634314663', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1333350, 'Angel Legion', 'Hi commanders! So glad to meet you!<br>Maybe you will feel tired or confused sometime when travel through the vast galaxy.Join our Steam group and let''s march together!<br>Website &quot;<strong><u><a href="https://s.team/chat/MG2jUjR9" target="_blank" rel="noreferrer"  >https://s.team/chat/MG2jUjR9</a></u></strong>&quot;<br>Our group welcomes all of you all the time!<br><br>Start an adventurous with commanders from all over the universe!<br>Lead your Angel team to reveal the ancient mysteries of the universe……<br><br><strong>#Game Features#</strong><br><strong>Gorgeous scene, Extreme experience</strong><br>3D dynamic scene, unlock when stage passed.<br>Vast Ocean of Stars, Immersive gaming experience.<br><br><strong>Idle Anywhere</strong><br>Idle anytime you want, play anywhere you wish.<br>Angel squad will fight for you automatically when you’re away.<br>Just wait and relax, you will be rewarded with huge amount of treasures.<br><br><strong>Customize faces of Heroes, Collect exquisite fashion</strong><br>You can customize heroes’ faces as you like.<br>Share the exquisite fashions with all your Angels.<br><br><strong>Evolving trick, Changing strategy</strong><br>You can summon nearly a hundred powerful heroes！<br>Forming a powerful Angel Squad, forging perfect equipments, and releasing effective skills to stand out in the Star Alliance.<br><br><strong>Boundless universe is waiting for you to explore</strong><br>In Angel Legion, not only can you explore the vast universe, you can also participate in Battlefield trials, you could even carry out the Expedition. Discover amazing treasure while you enjoying diverse gameplays.', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1333350/header.jpg?t=1649842585', CURRENT_DATE(), 'GAME', NULL);
