INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331930, 'Good N Dead', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1331930/extras/action-side.gif?t=1643791401" /><br><br>The dead are closing in on the last of the living, but you still have a few tricks up your sleeve. From barbed wire and flamethrowers to Tesla coils, your ability to build defensive structures in a pinch is unmatched. Go it alone or play with up to 4 friends fighting off increasingly larger waves of the dead in this zombie survival couch co-op tower defense shooter.<h2 class="bb_tag">Features:</h2><br><ul class="bb_ul"><li>Post-apocalyptic setting featuring a mix of indoor and outdoor levels with different challenges and enemies<br></li><li>Unlockable characters and weapons<br></li><li>Upgradable turrets and other defensive structures with unique abilities and effects<br></li><li>Open, grid-based building system for unlimited base designs<br></li><li>Local multiplayer for up to 4 players, including campaign co-op<br></li><li>Endless mode and global high score leaderboard on every level</li></ul>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331930/header.jpg?t=1643791401', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332120, 'VR Aim Trainer', 'The <strong>best</strong> way to warmup and improve your VR aim.<br><br>VR Aim Trainer provides highly customizable training scenarios to improve your VR skill, along with competitive leaderboards to prove your dominance in VR aiming.<h2 class="bb_tag">Scenarios</h2>You''ll find numerous scenarios for each skill involved in VR aiming: tracking, flicks, and hybrids of the two. Each of these scenarios provides a unique challenge, honing in on specific skill sets to provide a well-rounded warmup and challenge.<h2 class="bb_tag">Leaderboards</h2>Each scenario features its own competitive leaderboard, allowing you to gauge your skill level, climb the ranks, and see who is currently the best VR aimer.<h2 class="bb_tag">Customization</h2>Whether it''s your gun angle, colors of walls and targets, sound fx, handedness, or any other tweakable feature, you''ll be allowed to customize it to your preference. Comfort is key in VR, and finding your unique combination of settings is important in improving your aim, so customization is a top priority in VR Aim Trainer.', 6.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332120/header.jpg?t=1595703749', CURRENT_DATE(), 'GAME', NULL);
