INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332280, 'COVID-19  Epidemic Prevention', '通过酷鱼防疫宣传系列教学，用户可以避免去实体展馆带来的感染风险，只需要戴上VR设备就能真切地体验到身边常见的潜在灾害，学习关于新冠病毒的来源、传播途径及防治手段。', 169.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332280/header.jpg?t=1592297641', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332290, 'Spish', 'Fight alongside monsters to defeat your opponent in the arena<br><br>Control a character with a throwing spear against other player in this online PvP multiplayer game<h2 class="bb_tag">features:</h2><ul class="bb_ul"><li>Throwing spear as main weapon</li><li>Struck down chickens and fish to obtain item</li><li>Craft better monsters/spells</li></ul><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1332290/extras/spish_logo_sand_resized.png?t=1627206981" />', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332290/header.jpg?t=1627206981', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1332310, 'New Town Needs Tamer', 'Become a champion tamer for a newly formed town, full of people eager to meet you.<br><br>Settle down into the tamer life, going out on adventures to tame all kinds of creatures and becoming acquainted with the people of the town.<br><br><strong>Features:</strong><br><ul class="bb_ul"><li> A number of creatures to tame.<br></li><li> 3 Alternative looks for each creature, plus 2 color schemes for each.<br></li><li> 12 Dateable characters.<br></li><li> 6 Zones to explore.<br></li><li> A number of minigame moves for your creatures.<br></li><li> Over 300 customization items for your character.<br></li><li> 3 Different UI styles for you to choose.</li></ul><br><i>Expect to see more as development continues...</i>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1332310/header.jpg?t=1614328513', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331390, 'sfäre', 'Like all the best toys there''s no wrong way to play with sfäre. Its simple controls allow for an endless stream of entrancing new patterns that gently twist and pulse in a relaxing yet fascinating display.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331460, 'Super Gloves Hero', 'Click your way through 94 levels of 3d blocks blasting! Rotate, melt, break, blow up blocks to master your way to the top.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331510, 'Battle Talent', '<h1>Ver.0.0.2 Updated</h1><p>It’s just a quick fixed. Happy holidays everyone<br><br>Changed<br>* Increase the drop rate of coin<br>* Set EasySwing as default movement setup<br>* Optimize Tutorial level<br>* Fixed bugs in the sandbox custom button<br>Added<br>* Show tips when player use Oculus device running with SteamSDK, they should open Oculus.exe first to get better experience (not tested yet)<br>* Add version code on the message board<br>Removed<br>* The Climb Room( it’s not polished enough when using axe to climb)<br>What Coming Next:<br>* Try to add some parkour elements<br>* More language support<br>* Follow-up development outline</p><br><h1>About the Game</h1><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331510/extras/SteamPage.gif?t=1650462714" /><h2 class="bb_tag">What is Battle Talent</h2>Battle Talent  is a VR action game with Advanced Physics that satisfies your Combat Fantasy. <br>Let’s train yourself like a Warrior !<h2 class="bb_tag">It is suitable for:</h2><ul class="bb_ul"><li> players who like Creative Combat<br></li><li> like to Workout in VR<br></li><li> Challenge and Roguelite lover<br></li><li> VR enthusiast who wants to try Advanced Physics</li></ul><h2 class="bb_tag">Features</h2><ul class="bb_ul"><li> <strong><i>Advanced Physics</i></strong>: brings you accurate collision, climb, physics feedbacks and sounds, badass weapons and enemies…<br></li><li> <strong><i>Rich Combat Selections</i></strong>: aerial combo, deflect, grab, throw, dismember, shoot, magic…<br></li><li> <strong><i>Unique Weapons</i></strong>: many weapons have special skills, such as summoning lighting and ice<br></li><li> <strong><i>Roguelite Levels</i></strong>: massive seamless dungeon and lots of enemies, room events, loots waiting for you<br></li><li> <strong><i>Progress</i></strong>: you can earn coins and unlock new abilities and weapons in store as you played<br></li><li> <strong><i>Multiple Languages</i></strong> and <strong><i>Brutal Settings</i></strong> Supported</li></ul><h2 class="bb_tag">Ps</h2><ul class="bb_ul"><li> It is not easy to make a personal investment for two years, there is no form of crowdfunding, please join our Discord community, share, add a wish list to support us<br></li><li> The demo version only contains 90% of Trailer content<br></li><li> The image quality of Quest version is not exactly the same as Trailer<br></li><li> Seeking translation volunteers, your name will be shown in credits<br></li><li> We will checkout reviews and Discord almost everyday, please let us know if you have any disappointment or suggestion.<br></li><li> Before you start to play, make sure there are no people or monitor around you, In case of tragedy.</li></ul>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331510/header.jpg?t=1650462714', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331520, 'Across the Grooves Soundtrack', 'The <strong>Across the Grooves Original Soundtrack</strong> features more than 50 minutes of music and songs created exclusively for the game.<br><br>MP3-format files will be placed in your Across the Grooves folder in the Steam Directory: ...Steam\steamapps\common\Across the Grooves\Soundtrack<h2 class="bb_tag">Personnel:</h2><br><strong>Christelle Canot</strong> — Vocals, <br><strong>Charles-Henry Martin</strong> — Piano,<br><strong>Lili Richard-Gautier</strong> — Cello,<br><strong>Camille Marcos</strong> — Acoustic Guitars, Electric Guitars, Electric Bass, Percussions, Backing Vocals, <br><strong>Julien Ponsoda</strong> — Keyboards, Strings, Brass and Drums Programming, Percussions, Backing Vocals.<br><br>The <strong>Across the Grooves Original Soundtrack</strong> was composed and arranged by <strong>Camille Marcos</strong> and <strong>Julien Ponsoda</strong> (Illustrason).<br>All lyrics were written by <strong>Geoffroy Vincens</strong>.<br><br>The album was recorded, mixed, and mastered by <strong>Illustrason</strong> in Bordeaux in 2019 and 2020, and published by <strong>Nova-box</strong>.<h2 class="bb_tag">Additional Music:</h2><br><br><strong>Down to the Ground</strong>  by The Neo Tabou Project<br><br>Written, composed and arranged by Geoffroy Vincens.<br>Geoffroy Vincens — Vocals, Acoustic &amp; Electric Guitar, Acoustic Bass, Glockenspiel.<br>Maude Lamy de La Chapelle — Vocals.<br><br><strong>Chaos</strong>  by Asunder<br><br>Written by Geoffroy Vincens, composed by Jacques Malinvaud and arranged by Jacques Malinvaud &amp; Geoffroy Vincens.<br>Geoffroy Vincens — Vocals, Electric Guitars, Cymbals.<br>Jacques Malinvaud — Fretless Bass, Electric Guitars, Sound Effects and Drums Programming.', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331520/header.jpg?t=1592397861', CURRENT_DATE(), 'MUSIC', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331600, 'Holodeck Arena', 'Holodeck Arena (formerly Source Wars) is a fast-paced arcade RTS with base-building, resource management and territorial conquest, set in a tron-like environment. Battles are fought in virtual arenas that are divided into sectors. Each sector has it''s own mainframe temple, which can be hacked to take over the sector. The game is won, once all sectors are hacked and all enemy units destroyed.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331620, 'The Last Survey', 'The Last Survey is a narrative essay where you take on the role of a geologist contracted by a Brazilian mining company to survey rare earth metal reserves. Rare earth metals like nickel, silver, cobalt, and platinum are used in most major electronics including PC parts, green technology components, and almost all mobile devices. However, after a year of conducting your research, you''ve found that there is not enough of these metals on the planet to sustain industries reliant on current supply chains, and you must deliver these findings to the company''s CEO, Victor Ferreira.<br><br>Based on studies and projections from real-world geoscientists and mineral analysts, recent findings show there isn''t enough mineral material on the planet for the growing demands for electric cars, high efficiency fuel cells, solar paneling manufacturing, and even some of the components used to play &amp; produce this game.<br><br>Will your choices help steer the direction of your employer away from global mineral fatigue? Or will the urgency of your research fall on unsympathetic ears?<h2 class="bb_tag">This Game Features</h2><ul class="bb_ul"><li>Over 1500 of hand-drawn digital, graphite, and charcoal drawings<br></li><li>Choose-Your-Own Adventure monologue / dialog options<br></li><li>Multiple endings depending on how you conduct yourself<br></li><li>Dynamic soundtrack by Lewis Kopenhafer<br></li><li>Story-rich narrative that critiques globalism, extractionist capitalism, and executive greed</li></ul><h2 class="bb_tag">Praise for The Last Survey</h2>&quot;A gorgeously animated short game about confronting the limited resources this planet &amp; a chilling CEO... its haunting story has really stuck with me.&quot;<br>-Eli Cauley of <a href="https://steamcommunity.com/linkfilter/?url=http://&quot;https://crawlspacestudio.itch.io&quot;" target="_blank" rel="noopener"  >Crawlspace Studio</a><br><br>&quot;<i>The Last Survey </i>is certainly one of the most poignant games I have played in a long while, and it is exquisitely illustrated to boot.”<br>-Dhruv of <a href="https://steamcommunity.com/linkfilter/?url=http://&quot;https://store.steampowered.com/search/?developer=Studio%20Oleomingus&quot;" target="_blank" rel="noopener"  >Studio Oleomingus</a><br><br>&quot;I can''t think of many other games that absolutely nail the atmosphere of serious corporate power. Plus the art style and soundtrack are excellent!&quot;<br>-David Cribb, maker of <a href="https://steamcommunity.com/linkfilter/?url=http://&quot;https://store.steampowered.com/app/1229030/A_Hand_With_Many_Fingers/&quot;" target="_blank" rel="noopener"  >A Hand with Many Fingers</a><br><br>&quot;If there’s still an ongoing argument about whether visual novels are ‘games’, then The Last Survey is the conclusive answer.&quot;<br>-John Walker, co-founder of Rock Paper Shotgun', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331620/header.jpg?t=1603213557', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331660, 'RONY - Rise Of New York', '<strong>RONY - Rise Of New York</strong> is a vertical shoot''em up set in a dark New York.<br><br>*****<br><br>New York in a near future.<br>Weakened by a pandemic.<br>Taken over by an alien breed.<br><br>*****<br><br>In this retro action-packed vertical shooter, set in a weakened Big Apple taken over by an alien breed, you have to save the earth by savvy navigating your pimped spaceship through New York’s exciting skyline. <br><br>Flooded by magma, New York and the world need your help. You and your gang take on the challenge in New York.<br><br>The game will contain 20+ locations, 4 player characters, 60 missions, 50+ enemies, a level editor and hours of fun. <br><br>Save the city of New York from the invasion of energy-hungry aliens. Free the streets from the lava-draining factories. Restore the city to its former glory.<br><br><strong>Be part of the Rise Of New York!</strong>', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331660/header.jpg?t=1606219587', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330990, 'Bacuri', '<h1>Also recommended for you</h1><p><a href="https://store.steampowered.com/app/1559100/Miguelshroom_First_day_at_school/" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/1559100/Miguelshroom_First_day_at_school/</a><br><br><a href="https://store.steampowered.com/app/1486030/Hollow_Witch/" target="_blank" rel="noreferrer"  id="dynamiclink_1" >https://store.steampowered.com/app/1486030/Hollow_Witch/</a><br><br><a href="https://store.steampowered.com/app/1431450/Do_I_smell_Pizza/" target="_blank" rel="noreferrer"  id="dynamiclink_2" >https://store.steampowered.com/app/1431450/Do_I_smell_Pizza/</a><br><br><a href="https://store.steampowered.com/app/1486070/Zodiac/" target="_blank" rel="noreferrer"  >https://store.steampowered.com/app/1486070/Zodiac/</a></p><br><h1>About the Game</h1>One day a traveling cook found a newborn child eating inside a special fruit called Bacuri. The years have passed and a cheerful and partying young man travels with his cook uncle in search of new flavors. Today they arrived in the unknown realm of Ákañã and it seems that they smelled new flavors.<br>Discover Ákañã''s interconnected surreal kingdom plunging into a young man''s journey. Bacuri is a classic 2D action adventure where a boy discover himself, friends, habilities, lands, challenges and enemies. Draft your own path in this platformer crafted in modern pixel art style.<br><br>Game Characteristics:<br><br>- Classic screen scrolling action with all the modern improvements.<br><br>- Well-adjusted 2D controls. Dodge, attack and strike while facing challenges.<br><br>-Explore an interconnected world of forests and caves.<br><br>Forge your way! The Kingdom of Ákañã is expansive and open. Choose which paths to take, which friend to help.<br><br>-Evolve with new powerful skills! Climb the mountains, jump to new heights, glide and dart ahead in a swift flutter of hummingbird wings. Play your flute to enchant the forest and use darts with it to tame angry entities.<br><br>- When playing your flute, play a mini game in the best &quot;Guittar Hero&quot; style.<br><br>- Collect the local fruit in search of your fruit so loved by Bacuri.<br><br>- A cast of cute and scary characters revived in traditional pixel art animations frame by frame.<br><br>- Beautiful animated landscapes with parallax gives a depth of depth to a parallel world.<br><br>- Track inspired by Brazilian rhythms accompanies the player on his adventure. The music echoes the joy and sadness of a threatened and hopeful civilization.', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330990/header.jpg?t=1630520083', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331090, 'GoblinAttack / 进击的哥布林', 'The story tells how the Queen of Freyja of Galentis mainland, together with 7 loyal knights and protagonists, revives the kingdom.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331170, 'Mystery Trails', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1331170/extras/1_ENG.gif?t=1628246052" /><br><br>The year 1910, you’re a mountain rescuer and a hiking guide who has just received information about an accident in the area.<br>Your instinct tells you that this time it’s not going to be a regular one and it might be too late for the victims.<br>Soon after that you find a body and realize something strange happened out there, and it’s up to you to find out what it was.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331170/extras/2_ENG.gif?t=1628246052" /><br><br>In order to get the answers you’re seeking, you’ll also have to use your detective abilities.<br>You’re not a private investigator, but you’ll have to become one.<br>Think outside of the box, look for hidden details and interview potential witnesses.<br>Keep in mind that the weather won’t allow you to take it slow, time pressure is on you!<br><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331170/extras/3_ENG.gif?t=1628246052" /><br><br>Gather your equipment and make sure you’re confident in your own skills.<br>From climbing and skiing, through the proper reading of the map, all the way to tracking, avoiding and fighting animals.<br>There’s no room for error in this environment, so better keep your focus!<br>In the end, staying calm, cool and collected might save your life.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331170/extras/4_ENG.gif?t=1628246052" /><br><br>It''s always further than it looks, always taller than it looks, and always harder than it looks - the 3 rules of mountaineering. This isn’t another walk in the park.<br>Experience the harshest weather conditions and survive against them. Mother Nature doesn''t spoil you, and she surely shows no signs of mercy.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331170/extras/5_ENG.gif?t=1628246052" /><br><br>Explore the magnificent world of hiking, looking at it through the eyes of an experienced mountaineer.<br>Enjoy the breathtaking views and connect yourself with the natural world finding the freedom from worry, towards a true peace of mind!', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331170/header.jpg?t=1628246052', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1331340, 'Beat the Clock', '<img src="https://cdn.akamai.steamstatic.com/steam/apps/1331340/extras/Bandeau_BTC_616x64_no_background.gif?t=1630335813" /><br><br>Humanity has ended. An abyssal monster has awakened, determined to enslave to world.<br>But hope exists : if today that monstrous threat cannot be vanquished anymore, there were once a time when this creature was still vulnerable. Friedrich F, a genius chronomancer, plans to send the greatest heroes of all times in our past, at the moment when the invasion could still be stopped.<h2 class="bb_tag">Fonctionnalités</h2>• Form a team of 4 heroes amongst about <strong>20 availables</strong><br>• Explore a map composed of random events<br>• Face your foes in a unique turn-by-turn fight system<br>• Enhance your heroes'' capacities using capsules found on the way<br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331340/extras/BTC_heroes_steam.gif?t=1630335813" /><h2 class="bb_tag">Sent the greatest heroes back in time to save Humanity</h2> <br>At each new game, form a team of 4 legendary heroes amongst the 20 availables. Each hero has his own set of 3 skills and 2 passives to fullfil their role. Also, they all have an ultimate attack that can be unlocked during fights!<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331340/extras/BTC_RUN_steam.gif?t=1630335813" /><h2 class="bb_tag">Run, no time to waste</h2><br>As soon as arrived in the past, you''ll have to choose a path and explore a succession of random events : treasures, merchants, multiple choice dialogs,... and obviously difficult fights.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1331340/extras/BTC_fight_steam.gif?t=1630335813" /><h2 class="bb_tag">Unique turn by turn fights</h2><br>In Beat the Clock, the turn order defines the position of the heroes. The rightest hero will be the first to play, and will automatically be replaced behind after his action. Enemies are doing the same rotation, adding an interesting anticipation aspect to the strategy of the game.<h2 class="bb_tag">Beat the Clock is currently in Alpha and provides :</h2><br>8/20 Heroes - With a set of 3 active skills, 2 passives skills and a gradual ultimate skill.<br>9/30 Capsules - Essential components to upgrade your heroes during a game.<br>1/4 Biomes - Environments with their own identity, monsters and events.<br>5/20 Monsters - You like tentacles? So do we!<br>1/8 Mini-bosses - Created to harm you.<br>0/4 Bosses - They will be there soon enough.<br>4/40 Dialog events - Each providing 4 choices according to your team composition.', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1331340/header.jpg?t=1630335813', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330550, 'Magic Lessons in Wand Valley - a jigsaw puzzle tale', '<h1>Also recommended for you</h1><p><a href="https://store.steampowered.com/app/1477350/Jigsaw_Puzzle_Tales_Soundtrack/" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/1477350/Jigsaw_Puzzle_Tales_Soundtrack/</a><br><br><a href="https://store.steampowered.com/app/1399660/" target="_blank" rel="noreferrer"  id="dynamiclink_1" >https://store.steampowered.com/app/1399660/</a></p><br><h1>About the Game</h1>Once upon a time…<br>Leroy finally is an apprentice in the Wand Valley Magic School, which had been his dream since his earliest years. But lessons are so slow, could he learn faster? <br><br>Discover his story in six beautiful illustrations in this minimalist jigsaw puzzle game. <br><br><ul class="bb_ul"><li>Set up the jigsaw puzzle scenes at five different levels of difficulty - from 135 to 735 pieces.<br></li><li>Illustrations created especially for the game by artist Tiago Alvarez (@tiagoalvarez.art) with his creative art style.<br></li><li>Relax with an immersive and exclusive soundtrack.<br></li><li>Features include zooming the scene, highlighting border pieces, shuffling the remaining pieces, previewing the scene.<br></li><li>Complete the puzzle and unlock the image for download. Share it to express your achievements! Use it as a desktop background to inspire your day!</li></ul><br>Play this if you love fantasy stories and love jigsaw puzzles!<br><br>Have you ever learned something the hard way? Share your experience on our community forum.', 1.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330550/header.jpg?t=1635021229', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330570, 'RUN! GRANDPA! RUN!', 'It’s time for Grandpa to break free of the boring hospital life! Only you can help him escape, so grab your slippers, throw on your best slacks and strap in for an epic adventure for freedom in a rocket-powered wheelchair as you help Grandpa RUN!<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330570/extras/RGR_Gif_1_.gif?t=1648287149" /><br><br>In this manic, high-octane platformer, you’ll be pulling off some seriously impressive leaps as you traverse dangerous and surreal environments – all while evading the angry nurses who are hell-bent on cramping Grandpa’s style!<br><br>Race through the bizarre terrain as you prove that this old dog still has some tricks left in him, and his supercharged wheelchair. <br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330570/extras/RGR_Gif_3_.gif?t=1648287149" /><br><strong><h2 class="bb_tag">RUN! GRANDPA! RUN! Features</h2></strong>- A surreal and varied environment to navigate – what was in that medication!?<br>- Killer obstacles like swinging mallets and spinning blades which demand skilled steering.<br>- Rocket power-ups to fuel Grandpa’s epic jumps, but timing is key!<br>- New gameplay elements, including bouncing platforms, to keep the excitement up.', 4.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330570/header.jpg?t=1648287149', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330730, 'Under Domain - Alien Invasion Simulator', '<h1>ALSO RECOMMENDED FOR YOU</h1><p><a href="https://store.steampowered.com/app/1519280/Zombie_Neighborhood/" target="_blank" rel="noreferrer"  id="dynamiclink_0" >https://store.steampowered.com/app/1519280/Zombie_Neighborhood/</a></p><br><h1>About the Game</h1>Under Domain is a turn-based strategy game that invites you to create and execute an alien invasion plan directed to Planet Earth.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330730/extras/GIFDescricao.gif?t=1611327980" /><br><br>Transform yourself into the Reptilian leader that will lead your species to victory! Secretly influence human evolution by sabotaging their technologies, sending hybrids agents, executing operations of murder, mind-control, abductions, and virus contamination. Meanwhile, you build a powerful war fleet to attack at the right moment.<br><br>Under Domain accompanies human history from the 1900s until the year of 2020. The player has 10 movements each turn, and each movement represents one year of human existence, completing one decade at the end of each turn. Your actions must be accurate to avoid drawing the attention of humans. After all, possible alien spaceship sightings could reveal your existence to human Nations, and they can get ready to attack you first, increasing the difficulty level of the invasion phase. <br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330730/extras/LandBase2.png?t=1611327980" /><br><br>The game offers you simple ways of planning and executing the invasion, though with highly strategic combinations of actions that can lead you to different possible winning scenarios. Under Domain is a strategy game easy to learn but hard to master completely.<br><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330730/extras/Title1.png?t=1611327980" /><br><br><br>Explore the energy coming from pyramids, seek rare volcanic minerals to build your war machines, and launch orbital and surface probes to capture and interfere in human history''s relevant facts.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330730/extras/Title2.png?t=1611327980" /><br><br><br>Evolve your mothership and get access to new resources and instruments to raise your war power. Choose from 3 evolution pillars in each decision: Science, Military, or Infrastructure.<br><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330730/extras/Title3.png?t=1611327980" /><br><br><br>Replace key people on Earth''s evolution. You have to choose wisely which specialists will be able to sabotage human war power. <br><br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330730/extras/Title4.png?t=1611327980" /><br><br><br>Search, build, and enhance your spaceships. Spread them around Planet Earth, so in the moment of invasion, all of them rise in different locations to combat phase.<br><br>_', 4.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330730/header.jpg?t=1611327980', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330850, 'Shutter Heist', 'Shutter Heist is an 8-bit Crime Adventure, with a focus on quick moving, quick thinking, and legally debatable puzzles!<br><br><strong>&quot;Window shopping for the morally questionable!&quot;</strong><br><br><u>The Game</u><br><br>Shutter Heist is an 8-bit, Side-Scrolling, Crime Simulator at its core. Allowing for slow strategic game-play for those looking to be immersed, or fast paced, fast reaction, puzzle rapid fire for those living on the edge! Shutter Heist makes does not discriminate on criminal history, so the wide range of game-play options puts the heists success or failure in your hands! Your goal is to break in to as many storage units as you can, grab as much as you can carry and get out of there! However with rising crime level levels in the storage unit department has lead to a major upgrade in security. It is not as simple as hacking a various locks anymore... or is it?<br><br><u>The Game-play</u><br><br>Making your way through a storage depot and picking off the locks one by one may not sound too bad, but try to do it with a no nonsense security guard or is 3 weeks away from retirement breathing down your back. Luckily not only are you a master of hacking unique and surprisingly game like locks, you are also a master of stealth, being able to hide in almost any situation! Will you let him pass, or try to out run him? One thing is for sure, if you get caught, the heist is over!<br><br><u>The Puzzles</u><br><br>Shutter Heist contains over 8 types of reaction based and visual puzzles, each with over 100 variations total, keeping the player on their little 8-bit toes, every time! Simplistic design, and instructions aims to be easy to pick up, but hard to master, just like real crime!', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330850/header.jpg?t=1649621881', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330910, 'Johann Weiss Soundtrack', 'Johann Weiss is a campy retro action-adventure platformer combining the style and difficulty of play of a Metroidvania with the aesthetics and gameplay of the early Duke Nukem games. You’ll be wall-jumping your way into Hell as a demon-killing machine.<br />
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330930, 'Star Seeker in: the Secret of the Sorcerous Standoff', 'Good news: magic is real! Bad news: magic is <i>highly</i> illegal. Star Seeker is a <i>wizard</i> and you must solve a <i>wizard crime</i> or else get thrown in <i>wizard jail</i> by <i>wizard</i>- I mean, just regular police. In this short point ''n'' click adventure, collect evidence and use it to clear up the useless detective''s many confusions- and don''t worry about getting it wrong. Every hare-brained idea you can possibly pitch to the detective prompts unique dialogue!<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1330930/extras/S5_desc_banner_features.png?t=1604466759" /><br><ul class="bb_ul"><li>A short, one-room point-and-click mystery dense with clues<br></li><li>Unique responses for every combination of clue and question<br></li><li>Customizable pronouns in the options<br></li><li>Gorgeous pixelart and a magical original soundtrack (available from pastelhandgrenade on Bandcamp)<br></li><li>There is a goat in it, and it goes &quot;maaaaaa&quot;<br></li><li>If you don''t think that last one is a selling point then I don''t know what to tell you</li></ul>', 3.99, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330930/header.jpg?t=1604466759', CURRENT_DATE(), 'GAME', NULL);
INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1330080, 'The Fantastic Adventure of Monsieur Grape', 'The Fantastic Adventure of Monsieur Grape is a 2.5D platformer where you play as Monsieur Grape: A high brow, high class, grape who is trying to save his friend Peanut from an evil piece of bread! This game is a passion project designed to pay homage to popular platformers while scaling down graphics to enable playing on mobile devices.', 0, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1330080/header.jpg?t=1593792058', CURRENT_DATE(), 'GAME', NULL);
