INSERT INTO Article (id, title, description, price, currency, picture_url, release_date, article_type, id_base_article) VALUES (1328510, 'Save Iron Globe', 'Learn the story of Priscilla, the Perfect Soldier (PS) of the United Earth Federation, awakened from the cryptosleep to stop an alien invasion.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1328510/extras/steam1.png?t=1631141881" /><h2 class="bb_tag"> Stop the invasion </h2><br>Fight many of dangerous alliens across 10 levels to reach the orbit of the Moon and destroy the alien leader.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1328510/extras/steam2.gif?t=1631141881" /><h2 class="bb_tag"> Use bonuses </h2><br>Catch the supplies dropped by Federation''s Support to power up your ship.<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1328510/extras/steam4.png?t=1631141881" /><br><br><ul class="bb_ul"><li> <strong>Repair kit</strong> - restores 5 units of HP to your ship<br></li><li> <strong>Energy shield</strong> - protects the ship from receiving 10 damage units<br></li><li> <strong>Weapon booster</strong> - for 10 seconds activates an additional weapon module of the ship with more powerful guns<br></li><li> <strong>Stasis generator</strong> - slows down time by 10 seconds, helping to dodge opponents and their bullets</li></ul><h2 class="bb_tag"> Become the Perfect Soldier </h2><br>The passage of each level is rated:<br><br><ul class="bb_ul"><li> <strong>1 star</strong> - for passing<br></li><li> <strong>2 stars</strong> - for getting &gt;70% of the maximum PTS<br></li><li> <strong>3 stars</strong> - for getting &gt;90% of the maximum PTS</li></ul><br>Will you be able to complete all the levels and get 3 stars?<br><br><img src="https://cdn.akamai.steamstatic.com/steam/apps/1328510/extras/steam3.png?t=1631141881" />', 1.59, 'EUR', 'https://cdn.akamai.steamstatic.com/steam/apps/1328510/header.jpg?t=1631141881', CURRENT_DATE(), 'GAME', NULL);
